informalexample.programlisting:
void
foo_set_bar (Foo *foo,
             Bar *new_bar)
{
  g_return_if_fail (IS_FOO (foo));
  g_return_if_fail (new_bar == NULL || IS_BAR (new_bar));

  if (g_set_object (&foo->bar, new_bar))
    g_object_notify (foo, "bar");
}

void
foo_set_bar (Foo *foo,
             Bar *new_bar)
{
  g_return_if_fail (IS_FOO (foo));
  g_return_if_fail (new_bar == NULL || IS_BAR (new_bar));

  if (g_set_weak_pointer (&foo->bar, new_bar))
    g_object_notify (foo, "bar");
}

